import React, { useState } from "react";
import axios from "axios";
import { Document, Page, pdfjs } from "react-pdf";
import "react-pdf/dist/Page/TextLayer.css";
import "react-pdf/dist/Page/AnnotationLayer.css";
import "./App.css";

// Configure pdf.js worker
pdfjs.GlobalWorkerOptions.workerSrc = `//unpkg.com/pdfjs-dist@3.4.120/build/pdf.worker.min.js`;

function App() {
  const [file, setFile] = useState(null);
  const [fileURL, setFileURL] = useState(null);
  const [numPages, setNumPages] = useState(null);
  const [summary, setSummary] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleFileChange = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
    setFileURL(URL.createObjectURL(uploadedFile));
    setSummary("");
    setError("");
  };

  const handlePDFLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!file) {
      setError("Please upload a PDF file first.");
      return;
    }

    const formData = new FormData();
    formData.append("pdf", file);

    try {
      setLoading(true);
      setError("");
      setSummary("");

      const res = await axios.post(
        "http://localhost:5050/api/ai/summarize",
        formData,
        {
          headers: { "Content-Type": "multipart/form-data" },
        }
      );

      setSummary(res.data.summary || "No summary returned.");
    } catch (err) {
      console.error("‚ùå Upload Error:", err);
      setError(
        err.response?.data?.error ||
          "Failed to summarize the PDF. Please try again."
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-gray-50 to-gray-200 flex flex-col items-center justify-center p-6">
      <div className="bg-white shadow-xl rounded-2xl p-8 w-full max-w-5xl">
        <h1 className="text-3xl font-bold text-center text-blue-600 mb-4">
          üìÑ AI PDF Summarizer
        </h1>
        <p className="text-center text-gray-600 mb-6">
          Upload a PDF to preview and get an instant AI-generated summary.
        </p>

        <form
          onSubmit={handleSubmit}
          className="flex flex-col md:flex-row items-center gap-4 mb-6"
        >
          <input
            type="file"
            accept="application/pdf"
            onChange={handleFileChange}
            className="block w-full md:w-2/3 text-sm text-gray-700 
                       file:mr-4 file:py-2 file:px-4
                       file:rounded-lg file:border-0
                       file:text-sm file:font-semibold
                       file:bg-blue-50 file:text-blue-700
                       hover:file:bg-blue-100 cursor-pointer"
          />

          <button
            type="submit"
            disabled={loading}
            className="bg-blue-600 text-white px-6 py-2 rounded-lg 
                       hover:bg-blue-700 transition-all w-full md:w-1/3 
                       disabled:opacity-50 disabled:cursor-not-allowed"
          >
            {loading ? "Summarizing..." : "Summarize PDF"}
          </button>
        </form>

        {error && (
          <p className="text-red-600 text-center mt-4 bg-red-50 py-2 rounded-lg">
            ‚ö†Ô∏è {error}
          </p>
        )}

        <div className="flex flex-col md:flex-row gap-6">
          {/* PDF Preview Section */}
          {fileURL && (
            <div className="flex-1 border rounded-lg p-3 bg-gray-50 shadow-inner">
              <h2 className="text-lg font-semibold text-gray-700 mb-2">
                üìò PDF Preview ({numPages ? `${numPages} pages` : "Loading..."})
              </h2>
              <div className="overflow-auto max-h-[600px]">
                <Document
                  file={fileURL}
                  onLoadSuccess={handlePDFLoadSuccess}
                  className="flex flex-col items-center"
                >
                  {Array.from(new Array(numPages), (el, index) => (
                    <Page
                      key={`page_${index + 1}`}
                      pageNumber={index + 1}
                      width={350}
                    />
                  ))}
                </Document>
              </div>
            </div>
          )}

          {/* Summary Section */}
          <div className="flex-1 bg-gray-50 rounded-lg border border-gray-200 p-4">
            <h2 className="text-xl font-semibold mb-3 text-gray-700">
              üß† AI Summary
            </h2>
            {loading ? (
              <div className="flex justify-center mt-6">
                <div className="animate-spin rounded-full h-10 w-10 border-t-4 border-blue-500 border-solid"></div>
              </div>
            ) : summary ? (
              <p className="text-gray-800 whitespace-pre-line leading-relaxed">
                {summary}
              </p>
            ) : (
              <p className="text-gray-400 italic">
                Your summary will appear here after processing...
              </p>
            )}
          </div>
        </div>
      </div>

      <footer className="mt-8 text-gray-500 text-sm">
        Built with üíô using React, Tailwind, and OpenAI
      </footer>
    </div>
  );
}

export default App;
